mapper.select:
select i_num,i_shop,i_id,i_content,i_reg,i_img,i_placename,r 
from(select i_num,i_shop,i_id,i_content,i_reg,i_img,i_placename,rownum r 
from(select i_num,i_shop,i_id,i_content,i_reg,i_img,i_placename from infoboardreply where i_shop=#{number} order by i_reg desc)order by i_reg desc)where r>=#{startRow} and r<=#{endRow}
